                            value="unassigned"
                            data-testid="select-location-unassigned"
                          >
                            <div className="flex items-center gap-2">
                              <MapPin className="h-4 w-4 text-muted-foreground" />
                              {getLocationLabel("unassigned" as Location)}
                            </div>
                          </SelectItem>
                          {getVisibleLocations().filter(loc => loc !== "unassigned").map((location) => (
                            <SelectItem 
                              key={location} 
                              value={location}
                              data-testid={`select-location-${location}`}
                            >
                              <div className="flex items-center gap-2">
                                <MapPin className="h-4 w-4 text-muted-foreground" />
                                {getLocationLabel(location)}
                              </div>
                            </SelectItem>
                          ))}
                          {getCustomLocations().map((location) => (
                            <SelectItem 
                              key={location} 
                              value={location}
                              data-testid={`select-location-custom-${location}`}
                            >
                              <div className="flex items-center gap-2">
                                <MapPin className="h-4 w-4 text-muted-foreground" />
                                {location}
                              </div>
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />
            </div>

            <div className="grid grid-cols-2 gap-3">
              <FormField
                control={form.control}
                name="quantity"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Quantity</FormLabel>
                    <FormControl>
                      <div className="flex items-center gap-1">
                        <Button
                          type="button"
                          variant="outline"
                          size="icon"
                          className="shrink-0"
                          onClick={() => {
                            const current = parseFloat(field.value) || 1;
                            field.onChange(Math.max(0.01, current - 1));
                          }}
                          data-testid="button-quantity-minus"
                        >
                          <Minus className="h-4 w-4" />
                        </Button>
                        <Input
                          type="text"
                          inputMode="decimal"
                          className="text-center text-lg font-medium"
                          data-testid="input-quantity"
                          value={field.value ?? ""}
                          onChange={(e) => {
                            const val = e.target.value;
                            if (val === "" || /^\d*\.?\d*$/.test(val)) {
                              field.onChange(val === "" ? "" : val);
                            }
                          }}
                          onBlur={(e) => {
                            const parsed = parseFloat(e.target.value);
                            if (isNaN(parsed) || parsed < 0.01) {
                              field.onChange(1);
                            } else {
                              field.onChange(parsed);
                            }
                          }}
                        />
                        <Button
                          type="button"
                          variant="outline"
                          size="icon"
                          className="shrink-0"
                          onClick={() => {
                            const current = parseFloat(field.value) || 0;
                            field.onChange(current + 1);
                          }}
                          data-testid="button-quantity-plus"
                        >
                          <Plus className="h-4 w-4" />
                        </Button>
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="unit"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Unit</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-unit">
                          <SelectValue placeholder="Select" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {getVisibleUnits().map((unit) => (
                          <SelectItem 
                            key={unit.id} 
                            value={unit.id} 
                            data-testid={`select-unit-${unit.id}`}
                          >
                            {unit.singular === unit.plural 
                              ? unit.singular 
                              : `${unit.singular.charAt(0).toUpperCase() + unit.singular.slice(1)}(s)`}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="grid grid-cols-2 gap-3">
              <FormField
                control={form.control}
                name="expirationDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Use By Date</FormLabel>
                    <FormControl>
                      <Input
                        type="date"
                        data-testid="input-expiration-date"
                        {...field}
                        value={field.value || ""}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="lowStockThreshold"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Alert me when stock is</FormLabel>
                    <FormControl>
                      <Input
                        type="text"
                        inputMode="numeric"
                        pattern="[0-9]*"
                        placeholder="0"
                        data-testid="input-low-stock"
                        {...field}
                        value={field.value || ""}
                        onChange={(e) => field.onChange(parseInt(e.target.value) || 0)}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="space-y-2">
              <label className="text-base font-medium">Tags</label>
              <div className="flex flex-wrap gap-2">
                {getAvailableTags().map((tag) => (
                  <Badge
                    key={tag.id}
                    variant={selectedTags.includes(tag.id) ? "default" : "outline"}
                    className="cursor-pointer toggle-elevate"
                    onClick={() => toggleTag(tag.id)}
                    data-testid={`tag-${tag.id}`}
                  >
                    {tag.name}
                  </Badge>
                ))}
              </div>
            </div>

            <FormField
              control={form.control}
              name="notes"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Notes</FormLabel>
                  <FormControl>
                    <div className="relative">
                      <Input
                        placeholder="Additional notes..."
                        data-testid="input-notes"
                        value={notesInputValue}
                        onChange={(e) => {
                          setNotesInputValue(e.target.value);
                          field.onChange(e.target.value);
                        }}
                        onFocus={() => setShowNotesSuggestions(true)}
                        onBlur={() => setTimeout(() => setShowNotesSuggestions(false), 150)}
                      />
                      {showNotesSuggestions && filteredNotes.length > 0 && (
                        <div className="absolute z-50 w-full mt-1 bg-popover border rounded-md shadow-md">
                          {filteredNotes.map((note) => (
                            <button
                              key={note}
                              type="button"
                              className="w-full px-3 py-2 text-left text-sm hover:bg-accent hover:text-accent-foreground first:rounded-t-md last:rounded-b-md"
                              onMouseDown={(e) => {
                                e.preventDefault();
                                setNotesInputValue(note);
                                field.onChange(note);
                                setShowNotesSuggestions(false);
                              }}
                              data-testid={`notes-suggestion-${note.replace(/\s+/g, "-").toLowerCase()}`}
                            >
                              {note}
                            </button>
                          ))}
                        </div>
                      )}
                    </div>
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {isEditing && item?.createdAt && (
              <div className="text-xs text-muted-foreground text-center pt-4 border-t">
                Added on {format(new Date(item.createdAt), `${getDateFormat()} 'at' h:mm a`)}
              </div>
            )}
          </form>
        </Form>
      </div>

      <div className="fixed bottom-0 left-0 right-0 bg-background border-t p-4">
        <div className="flex gap-3">
          <Button
            type="button"
            variant="outline"
            className="flex-1"
            onClick={() => navigate("/")}
            data-testid="button-cancel"
          >
            Cancel
          </Button>
          {isEditing && (
            <Button
              type="button"
              variant="destructive"
              size="icon"
              onClick={() => deleteMutation.mutate()}
              disabled={deleteMutation.isPending}
              data-testid="button-delete"
            >
              {deleteMutation.isPending ? <Loader2 className="h-4 w-4 animate-spin" /> : <Trash2 className="h-4 w-4" />}
            </Button>
          )}
          <Button 
            type="button"
            className="flex-1"
            onClick={form.handleSubmit(handleSubmit)}
            disabled={isLoading}
            data-testid="button-save"
          >
            {isLoading && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
            {isEditing ? "Save" : "Add"}
          </Button>
        </div>
      </div>

      <BarcodeScanner
        open={isScannerOpen}
        onOpenChange={setIsScannerOpen}
        onBarcodeScanned={handleBarcodeDetected}
      />
    </div>
  );
}

client/src/components/shopping-list-page.content:
import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { CategoryIcon } from "@/components/category-icon";
import { ShoppingCart, Check, Share2, Mail, MessageCircle, Copy, CheckCheck } from "lucide-react";
import { SiWhatsapp } from "react-icons/si";
import { type FreezerItem, type Location } from "@shared/schema";
import { getLocationLabel, getUnitLabelConfig } from "@/components/settings-panel";
import { useToast } from "@/hooks/use-toast";

const weightUnits = ["lb", "kg", "oz", "g"];

function formatQuantity(quantity: number, unit: string): string {
  if (weightUnits.includes(unit)) {
    return quantity % 1 === 0 ? quantity.toString() : quantity.toFixed(2);
  }
  return Math.round(quantity).toString();
}

function getUnitLabel(unit: string, quantity: number): string {
  const labels = getUnitLabelConfig(unit);
  if (labels) {
    const displayQty = weightUnits.includes(unit) ? quantity : Math.round(quantity);
    return displayQty === 1 ? labels.singular : labels.plural;
  }
  return unit;
}

interface ShoppingListPageProps {
  items: FreezerItem[];
  onEditItem: (item: FreezerItem) => void;
}

function getLowStockItems(items: FreezerItem[]): FreezerItem[] {
  return items.filter(
    (item) => (item.lowStockThreshold ?? 0) > 0 && item.quantity <= (item.lowStockThreshold ?? 0)
  );
}

export function ShoppingListPage({ items, onEditItem }: ShoppingListPageProps) {
  const lowStockItems = getLowStockItems(items);
  const [copied, setCopied] = useState(false);
  const { toast } = useToast();

  const generateListText = () => {
    if (lowStockItems.length === 0) return "";

    const header = "Shopping List - Items to Restock:\n\n";
    const itemLines = lowStockItems.map(item => {
      const needed = Math.max(0.01, (item.lowStockThreshold ?? 0) - Number(item.quantity));
      return `- ${item.name}: need ${formatQuantity(needed, item.unit)} ${getUnitLabel(item.unit, needed)}`;
    }).join("\n");

    return header + itemLines;
  };

  const handleShare = async () => {
    const text = generateListText();
    if (!text) return;

    if (navigator.share) {
      try {
        await navigator.share({
          title: "Shopping List",
          text: text,
        });
      } catch (err) {
        // User cancelled or error
      }
    }
  };

  const handleCopy = async () => {
    const text = generateListText();
    if (!text) return;

    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      toast({ title: "Copied to clipboard" });
      setTimeout(() => setCopied(false), 2000);
    } catch {
      toast({ title: "Failed to copy", variant: "destructive" });
    }
  };

  const handleEmail = () => {
    const text = generateListText();
    if (!text) return;

    const subject = encodeURIComponent("Shopping List - Items to Restock");
    const body = encodeURIComponent(text);
    window.open(`mailto:?subject=${subject}&body=${body}`, "_blank");
  };

  const handleWhatsApp = () => {
    const text = generateListText();
    if (!text) return;

    const encoded = encodeURIComponent(text);
    window.open(`https://wa.me/?text=${encoded}`, "_blank");
  };

  const handleSMS = () => {
    const text = generateListText();
    if (!text) return;

    const encoded = encodeURIComponent(text);
    window.open(`sms:?body=${encoded}`, "_blank");
  };

  return (
    <div className="p-4 space-y-4 pb-32">
      <div className="flex items-center gap-2">
        <ShoppingCart className="h-5 w-5 text-primary" />
        <h1 className="text-lg font-semibold">Shopping List</h1>
      </div>

      {lowStockItems.length === 0 ? (
        <div className="text-center py-12">
          <Check className="h-12 w-12 mx-auto mb-4 text-green-500 opacity-50" />
          <p className="text-muted-foreground">Your freezer is well stocked!</p>
          <p className="text-sm text-muted-foreground">
            Set low stock thresholds on items to see them here when running low.
          </p>
        </div>
      ) : (
        <div className="space-y-3">
          <p className="text-sm text-muted-foreground">
            {lowStockItems.length} {lowStockItems.length === 1 ? "item" : "items"} to restock
          </p>

          {lowStockItems.map((item) => (
            <Card 
              key={item.id} 
              className="hover-elevate cursor-pointer"
              onClick={() => onEditItem(item)}
              data-testid={`card-shopping-${item.id}`}
            >
              <CardContent className="p-4">
                <div className="flex items-center justify-between gap-3">
                  <div className="flex items-center gap-3 flex-1 min-w-0">
                    <CategoryIcon category={item.category} size="lg" />
                    <div className="flex-1 min-w-0">
                      <p className="font-medium truncate">{item.name}</p>
                    </div>
                  </div>
                  <div className="text-right space-y-0.5">
                    <p className="text-xs text-muted-foreground">
                      In stock: <span className="font-medium text-foreground">{formatQuantity(Number(item.quantity), item.unit)} {getUnitLabel(item.unit, Number(item.quantity))}</span>
                    </p>
                    <p className="text-xs text-muted-foreground">
                      Minimum: <span className="font-medium text-foreground">{formatQuantity(item.lowStockThreshold ?? 0, item.unit)} {getUnitLabel(item.unit, item.lowStockThreshold ?? 0)}</span>
                    </p>
                    <Badge variant="destructive" className="mt-1">
                      Need {formatQuantity(Math.max(0.01, (item.lowStockThreshold ?? 0) - Number(item.quantity)), item.unit)} more {getUnitLabel(item.unit, Math.max(1, (item.lowStockThreshold ?? 0) - Number(item.quantity)))}
                    </Badge>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}

          <Card className="mt-6 bg-muted/50">
            <CardContent className="p-4">
              <div className="flex items-center justify-center gap-2 mb-3">
                <Share2 className="h-4 w-4 text-muted-foreground" />
                <span className="text-sm font-medium">Share this list with someone else</span>
              </div>
              <div className="flex flex-wrap justify-center gap-2">
                {typeof navigator !== 'undefined' && navigator.share && (
                  <Button 
                    variant="outline" 
                    onClick={handleShare}
                    data-testid="button-share-native"
                  >
                    <Share2 className="h-4 w-4 mr-2" />
                    Share
                  </Button>
                )}
                <Button 
                  variant="outline" 
                  onClick={handleWhatsApp}
                  data-testid="button-share-whatsapp"
                >
                  <SiWhatsapp className="h-4 w-4 mr-2" />
                  WhatsApp
                </Button>
                <Button 
                  variant="outline" 
                  onClick={handleEmail}
                  data-testid="button-share-email"
                >
                  <Mail className="h-4 w-4 mr-2" />
                  Email
                </Button>
                <Button 
                  variant="outline" 
                  onClick={handleSMS}
                  data-testid="button-share-sms"
                >
                  <MessageCircle className="h-4 w-4 mr-2" />
                  SMS
                </Button>
                <Button 
                  variant="outline" 
                  onClick={handleCopy}
                  data-testid="button-share-copy"
                >
                  {copied ? <CheckCheck className="h-4 w-4 mr-2" /> : <Copy className="h-4 w-4 mr-2" />}
                  {copied ? "Copied" : "Copy"}
                </Button>
              </div>
            </CardContent>
          </Card>
        </div>
      )}
    </div>
  );
}

export function getListCount(items: FreezerItem[]): number {
  return getLowStockItems(items).length;
}

client/src/components/alerts-page.tsx.content:
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { CategoryIcon } from "@/components/category-icon";
import { ExpirationBadge } from "@/components/expiration-badge";
import { AlertTriangle, Clock, Calendar } from "lucide-react";
import { type FreezerItem, type Location } from "@shared/schema";
import { getLocationLabel, getDateFormat, getFreezerLabel } from "@/components/settings-panel";
import { format, parseISO, isValid, differenceInDays, isToday, isPast } from "date-fns";

interface AlertsPageProps {
  items: FreezerItem[];
  onEditItem: (item: FreezerItem) => void;
}

function getExpirationStatus(expirationDate: string | null) {
  if (!expirationDate || !isValid(parseISO(expirationDate))) {
    return { status: "none", label: "No date", color: "text-muted-foreground" };
  }

  const date = parseISO(expirationDate);
  const daysUntil = differenceInDays(date, new Date());

  if (isPast(date) && !isToday(date)) {
    return { status: "expired", label: "Expired", color: "text-destructive" };
  }
  if (isToday(date)) {
    return { status: "today", label: "Expires today", color: "text-orange-500" };
  }
  if (daysUntil <= 7) {
    return { status: "soon", label: `${daysUntil} days left`, color: "text-yellow-500" };
  }
  return { status: "ok", label: `${daysUntil} days left`, color: "text-green-500" };
}

function getDaysLeftText(expirationDate: string | null): { text: string; daysLeft: number } | null {
  if (!expirationDate || !isValid(parseISO(expirationDate))) return null;

  const date = parseISO(expirationDate);
  const daysLeft = differenceInDays(date, new Date());

  if (isPast(date) && !isToday(date)) {
    const daysOverdue = Math.abs(daysLeft);
    return { text: `${daysOverdue} day${daysOverdue !== 1 ? 's' : ''} overdue`, daysLeft };
  }
  if (isToday(date)) {
    return { text: "0 days left", daysLeft: 0 };
  }
  return { text: `${daysLeft} day${daysLeft !== 1 ? 's' : ''} left`, daysLeft };
}

function ExpirationCard({ item, onEdit }: { item: FreezerItem; onEdit: () => void }) {
  const status = getExpirationStatus(item.expirationDate);
  const formattedDate = item.expirationDate && isValid(parseISO(item.expirationDate))
    ? format(parseISO(item.expirationDate), getDateFormat())
    : null;
  const daysInfo = getDaysLeftText(item.expirationDate);
  const formattedAddedDate = item.createdAt 
    ? format(new Date(item.createdAt), getDateFormat())
    : null;

  return (
    <Card 
      className="hover-elevate cursor-pointer" 
      onClick={onEdit}
      data-testid={`card-alert-${item.id}`}
    >
      <CardContent className="p-4">
        <div className="flex items-start justify-between gap-3">
          <div className="flex items-center gap-3 flex-1 min-w-0">
            <CategoryIcon category={item.category} size="lg" />
            <div className="flex-1 min-w-0">
              <p className="font-medium truncate">
                {item.name} <span className="font-normal text-muted-foreground">({item.quantity} {item.unit})</span>
              </p>
              <p className="text-xs text-muted-foreground">
                {getFreezerLabel(item.freezerId)}
              </p>
            </div>
          </div>
          <div className="text-right flex flex-col items-end gap-1">
            <ExpirationBadge expirationDate={item.expirationDate} />
            {formattedDate && (
              <p className="text-xs text-muted-foreground">Use by date: {formattedDate}</p>
            )}
            {formattedAddedDate && (
              <p className="text-xs text-muted-foreground">Date added: {formattedAddedDate}</p>
            )}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

export function AlertsPage({ items, onEditItem }: AlertsPageProps) {
  const itemsWithExpiration = items.filter(
    (item) => item.expirationDate && isValid(parseISO(item.expirationDate))
  );

  const expiredItems = itemsWithExpiration.filter((item) => {
    const date = parseISO(item.expirationDate!);
    return isPast(date) && !isToday(date);
  });

  const expiringTodayItems = itemsWithExpiration.filter((item) => {
    return isToday(parseISO(item.expirationDate!));
  });

  const expiringSoonItems = itemsWithExpiration.filter((item) => {
    const date = parseISO(item.expirationDate!);
    const daysUntil = differenceInDays(date, new Date());
    return daysUntil > 0 && daysUntil <= 7;
  });

  const hasAlerts = expiredItems.length > 0 || expiringTodayItems.length > 0 || expiringSoonItems.length > 0;

  return (
    <div className="p-4 space-y-6">
      <div className="flex items-center gap-2">
        <AlertTriangle className="h-5 w-5 text-destructive" />
        <h1 className="text-lg font-semibold">Use By Alerts</h1>
      </div>

      {!hasAlerts ? (
        <div className="text-center py-12">
          <Calendar className="h-12 w-12 mx-auto mb-4 text-muted-foreground opacity-50" />
          <p className="text-muted-foreground">No use by alerts</p>
          <p className="text-sm text-muted-foreground">All your items are fresh!</p>
        </div>
      ) : (
        <>
          {expiredItems.length > 0 && (
            <div className="space-y-3">
              <h2 className="text-sm font-medium text-destructive flex items-center gap-2">
                <AlertTriangle className="h-4 w-4" />
                Expired ({expiredItems.length})
              </h2>
              {expiredItems.map((item) => (
                <ExpirationCard key={item.id} item={item} onEdit={() => onEditItem(item)} />
              ))}
            </div>
          )}

          {expiringTodayItems.length > 0 && (
            <div className="space-y-3">
              <h2 className="text-sm font-medium text-orange-500 flex items-center gap-2">
                <Clock className="h-4 w-4" />
                Use By Today ({expiringTodayItems.length})
              </h2>
              {expiringTodayItems.map((item) => (
                <ExpirationCard key={item.id} item={item} onEdit={() => onEditItem(item)} />
              ))}
            </div>
          )}

          {expiringSoonItems.length > 0 && (
            <div className="space-y-3">
              {expiringSoonItems.map((item) => (
                <ExpirationCard key={item.id} item={item} onEdit={() => onEditItem(item)} />
              ))}
            </div>
          )}
        </>
      )}
    </div>
  );
}

export function getAlertCount(items: FreezerItem[]): number {
  return items.filter((item) => {
    if (!item.expirationDate || !isValid(parseISO(item.expirationDate))) return false;
    const date = parseISO(item.expirationDate);
    const daysUntil = differenceInDays(date, new Date());
    return isPast(date) || daysUntil <= 7;
  }).length;
}

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(claims: any) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  const registeredStrategies = new Set<string>();

  const ensureStrategy = (domain: string) => {
    const strategyName = `replitauth:${domain}`;
    if (!registeredStrategies.has(strategyName)) {
      const strategy = new Strategy(
        {
          name: strategyName,
          config,
          scope: "openid email profile offline_access",
          callbackURL: `https://${domain}/api/callback`,
        },
        verify,
      );
      passport.use(strategy);
      registeredStrategies.add(strategyName);
    }
  };

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    ensureStrategy(req.hostname);
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    ensureStrategy(req.hostname);
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

